import logo from '../logo.svg';
import '../App.css';
import { useSelector,useDispatch,dispatch } from 'react-redux';
import { getList } from '../redux/action/updateListActionComponent';
import { iconAdd } from 'carbon-icons';
import ReactDOM from 'react-dom';



import {
  DataTable,
  Table,
  TableHead,
  TableRow,
  TableHeader,
  TableBody,
  TableCell,
  TableContainer,
  TableToolbar,
  TableToolbarContent,
  TableToolbarSearch,
  TableToolbarMenu,
  TableToolbarAction,
  TableSelectAll,
  TableSelectRow,
  TableBatchActions,
  TableBatchAction,
  Button,
  ComposedModal
} from 'carbon-components-react';

import React, { useState,useEffect } from 'react';

function action(act){
  console.log(act)
}
function deleteBatch(act){
  console.log('in deleteBatch')
  console.log(act)
}
function deactivateBatch(act){
  console.log(' in deactivateBatch')
  console.log(act)
  var deactivate_list = [];
  act.forEach(function(user){
    deactivate_list.push(parseInt(user.id))
  });
  const requestOptions = {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(
      {

          "list":deactivate_list
      })
  };
  fetch('http://127.0.0.1:8000/users/user-deactivate/', requestOptions)

}
function activateBatch(act){
  console.log('in activateBatch ')
  console.log(act)
  var activate_list = [];
  act.forEach(function(user){
    activate_list.push(parseInt(user.id))
  });
  const requestOptions = {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(
      {

          "list":activate_list
      })
  };
  fetch('http://127.0.0.1:8000/users/user-activate/', requestOptions)
  // .then(response => response.json())
  //       .then(data => this.setState());



}



const headers = [
  {
    key: 'first_name',
    header: 'Name',
  },
  {
    key: 'username',
    header: 'Username',
  },
  {
    key: 'is_superuser',
    header: 'Superuser',
  },
  {
    key: 'is_staff',
    header: 'Staff',
  },
  {
    key: 'is_active',
    header: 'Active',
  },
];


const List = ()=>{
  const [rows,setRows] = useState([]);
  const[loading,setLoading] = useState(true);
  const [open, setOpen] = useState(false);
  const dispatch = useDispatch()
//   const[error,setError] = 
const data = useSelector(state=>state.updateListReducer.List)
console.log(rows)
useEffect(()=>{

dispatch(getList())
},[])
useEffect(()=>{
setRows(data)
},[data])
  return (<>
  {typeof document === 'undefined'
        ? null
        : ReactDOM.createPortal(
            <ComposedModal open={open} onClose={() => setOpen(false)}>
              sdkjfhskdljfhslkdjfhlskdjfhlskdjfhlskdjfhlskdjfhlskdjfhlskdf
            </ComposedModal>,
            document.body
          )}


  {rows?.length!==0?
    <DataTable rows={rows} headers={headers}>
    {({
      rows,
      headers,
      getHeaderProps,
      getRowProps,
      getSelectionProps,
      getToolbarProps,
      getBatchActionProps,
      onInputChange,
      selectedRows,
      getTableProps,
      getTableContainerProps,
    }) => (
      <TableContainer
      title="Users"

      {...getTableContainerProps()}>
      <TableToolbar {...getToolbarProps()}>
        <TableBatchActions {...getBatchActionProps()}>
          <TableBatchAction
            tabIndex={getBatchActionProps().shouldShowBatchActions ? 0 : -1}

            onClick={() => { deleteBatch(selectedRows);dispatch(getList())}}>
            Delete
          </TableBatchAction>
          <TableBatchAction
            tabIndex={getBatchActionProps().shouldShowBatchActions ? 0 : -1}

            onClick={() => {activateBatch(selectedRows);dispatch(getList())}}>
            Activate
          </TableBatchAction>
          <TableBatchAction
            tabIndex={getBatchActionProps().shouldShowBatchActions ? 0 : -1}

            onClick={() => {deactivateBatch(selectedRows);dispatch(getList())}}>
            Deactivate
          </TableBatchAction>
        </TableBatchActions>
        <TableToolbarContent>
          <TableToolbarSearch
            persistent="true"
            tabIndex={getBatchActionProps().shouldShowBatchActions ? -1 : 0}
            onChange={onInputChange}
          />
          <TableToolbarMenu
            tabIndex={getBatchActionProps().shouldShowBatchActions ? -1 : 0}>
            <TableToolbarAction onClick={() => alert('Alert 1')}>
              Action 1
            </TableToolbarAction>
            <TableToolbarAction onClick={() => alert('Alert 2')}>
              Action 2
            </TableToolbarAction>
            <TableToolbarAction onClick={() => alert('Alert 3')}>
              Action 3
            </TableToolbarAction>
          </TableToolbarMenu>
          <Button
            tabIndex={getBatchActionProps().shouldShowBatchActions ? -1 : 0}
            onClick={() => setOpen(true)}
            size="small"
            kind="primary">
            Add new
          </Button>
        </TableToolbarContent>
      </TableToolbar>
      <Table {...getTableProps()}>
        <TableHead>
          <TableRow>
            <TableSelectAll {...getSelectionProps()} />
            {headers.map((header, i) => (
              <TableHeader key={i} {...getHeaderProps({ header,isSortable: true })}>
                {header.header}
              </TableHeader>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row, i) => (
            <TableRow key={i} {...getRowProps({ row })}>
              <TableSelectRow {...getSelectionProps({ row })} />
              {row.cells.map((cell) => (
                                 <TableCell key={cell.id}>{
                                  // console.log(cell.value)
                                  [true,false].includes(cell.value) ? (
                                                                            cell.value ? (
                                                              <img  width="10" height="10" src="/check.png" />
                                                                                          ) : (
                                                                  <img width="10" height="10" src="/cancel.png" />
                                                                        )
                                                      ):cell.value




                                }</TableCell>
              ))}
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    )}
  </DataTable>
    :null}</>
  );
}

export default List;
