import logo from '../logo.svg';
import '../App.css';
import { useSelector,useDispatch } from 'react-redux';
import { getList } from '../redux/action/updateListActionComponent';



import {
  DataTable,
  Table,
  TableHead,
  TableRow,
  TableHeader,
  TableBody,
  TableCell,
  TableToolbar,
  TableToolbarContent,
  TableToolbarSearch,
  onInputChange,
  TableToolbarMenu,
  TableToolbarAction,
  Button
} from 'carbon-components-react';
import React, { useState,useEffect } from 'react';


const headers = [
  {
    key: 'first_name',
    header: 'Name',
  },
  {
    key: 'username',
    header: 'Username',
  },
  {
    key: 'is_superuser',
    header: 'Superuser',
  },
  {
    key: 'is_staff',
    header: 'Staff',
  },
  {
    key: 'is_active',
    header: 'Active',
  },
];


const List = ()=>{
  const [rows,setRows] = useState([]);
  const[loading,setLoading] = useState(true);
  const dispatch = useDispatch()
//   const[error,setError] = 
const data = useSelector(state=>state.updateListReducer.List)
console.log(rows)
useEffect(()=>{

dispatch(getList())
},[])
useEffect(()=>{
setRows(data)
},[data])
  return (<>{rows?.length!==0?
<DataTable rows={rows} headers={headers}>
<TableToolbar>
          <TableToolbarContent>
            {/* pass in `onInputChange` change here to make filtering work */}
            <TableToolbarSearch  />
            <TableToolbarMenu>
              <TableToolbarAction >
                Action 1
              </TableToolbarAction>
              <TableToolbarAction >
                Action 2
              </TableToolbarAction>
              <TableToolbarAction >
                Action 3
              </TableToolbarAction>
            </TableToolbarMenu>
            <Button >Primary Button</Button>
          </TableToolbarContent>
        </TableToolbar>




  {({ rows, headers, getTableProps, getHeaderProps, getRowProps }) => (
    <Table {...getTableProps()}>
      <TableHead>
        <TableRow>
          {headers.map((header) => (
            <TableHeader {...getHeaderProps({ header,isSortable: true,showToolbar: true,showHeaders: true })}>
              {header.header}
            </TableHeader>
          ))}
        </TableRow>
      </TableHead>
      <TableBody>
        {rows.map((row) => (
            console.log(row),
          <TableRow {...getRowProps({ row })}>
            {row.cells.map((cell) => (

              <TableCell key={cell.id}>{
                // console.log(cell.value)
                [true,false].includes(cell.value) ? (
                                                          cell.value ? (
                                            <img  width="10" height="10" src="/check.png" />
                                                                        ) : (
                                                <img width="10" height="10" src="/cancel.png" />
                                                      )
                                    ):cell.value




              }</TableCell>
            ))}
          </TableRow>
        ))}
      </TableBody>
    </Table>
  )}
</DataTable>:null}</>
  );
}

export default List;
