import logo from '../logo.svg';
import '../App.css';
import { useSelector,useDispatch,dispatch } from 'react-redux';
import { getList } from '../redux/action/updateListActionComponent';



import {
  DataTable,
  Table,
  TableHead,
  TableRow,
  TableHeader,
  TableBody,
  TableCell,
  TableContainer,
  TableToolbar,
  TableToolbarContent,
  TableToolbarSearch,
  TableToolbarMenu,
  TableToolbarAction,
  TableSelectAll,
  TableSelectRow,
  Button
} from 'carbon-components-react';
import React, { useState,useEffect } from 'react';


const headers = [
  {
    key: 'first_name',
    header: 'Name',
  },
  {
    key: 'username',
    header: 'Username',
  },
  {
    key: 'is_superuser',
    header: 'Superuser',
  },
  {
    key: 'is_staff',
    header: 'Staff',
  },
  {
    key: 'is_active',
    header: 'Active',
  },
];


const List = ()=>{
  const [rows,setRows] = useState([]);
  const[loading,setLoading] = useState(true);
  const dispatch = useDispatch()
//   const[error,setError] = 
const data = useSelector(state=>state.updateListReducer.List)
console.log(rows)
useEffect(()=>{

dispatch(getList())
},[])
useEffect(()=>{
setRows(data)
},[data])
  return (<>{rows?.length!==0?
    <DataTable rows={rows} headers={headers}>
    {({
      rows,
      headers,
      getHeaderProps,
      getRowProps,
      getSelectionProps,
      getToolbarProps,
      getBatchActionProps,
      onInputChange,
      selectedRows,
      getTableProps,
      getTableContainerProps,
    }) => (
      <TableContainer title="Users" >
        <TableToolbar>
          <TableToolbarContent>
            {/* pass in `onInputChange` change here to make filtering work */}
            <TableToolbarSearch onChange={onInputChange} />
            <TableToolbarMenu>
              <TableToolbarAction >
                Action 1
              </TableToolbarAction>
              <TableToolbarAction >
                Action 2
              </TableToolbarAction>
              <TableToolbarAction >
                Action 3
              </TableToolbarAction>
            </TableToolbarMenu>
            <Button >Add new</Button>
          </TableToolbarContent>
        </TableToolbar>
        <Table {...getTableProps()}>
          <TableHead>
            <TableRow>
            <TableSelectAll {...getSelectionProps()} />
              {headers.map((header) => (
                <TableHeader key={header.key} {...getHeaderProps({ header,isSortable: true })}>
                  {header.header}
                </TableHeader>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <TableRow key={row.id} {...getRowProps({ row })}>
                <TableSelectRow {...getSelectionProps({ row })} />
                {row.cells.map((cell) => (
                  <TableCell key={cell.id}>{
                    // console.log(cell.value)
                    [true,false].includes(cell.value) ? (
                                                              cell.value ? (
                                                <img  width="10" height="10" src="/check.png" />
                                                                            ) : (
                                                    <img width="10" height="10" src="/cancel.png" />
                                                          )
                                        ):cell.value




                  }</TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    )}
  </DataTable>
    :null}</>
  );
}

export default List;
